// src/pages/CitizenView.jsx
import React from 'react';
import { GoogleMap, Marker } from '@react-google-maps/api';
import { useFetchIncidents } from '../Hooks/useFetchIncidents';

const CitizenView = () => {
  const { incidents, loading } = useFetchIncidents();

  const mapStyles = {
    width: '100%',
    height: '100%',
  };
  const iconSize = new window.google.maps.Size(32, 32); // Tamaño fijo para todos los íconos

  return (
    <div className="min-h-screen bg-white flex flex-col lg:flex-row items-start ">
      {/* Contenedor del mapa */}
      <div className="w-full lg:w-3/5 h-80 lg:h-screen mb-6 lg:mb-0">
        <GoogleMap
          center={{ lat: 4.142, lng: -73.626 }}
          zoom={13}
          mapContainerStyle={mapStyles}
          options={{
            disableDefaultUI: true,
            clickableIcons: false,
          }}
        >
 {incidents.map((incident) => {
    let iconUrl = ''; // Variable para almacenar la URL del icono

    // Switch para elegir la URL del icono en función del tipo de incidente
    switch (incident.type) {
      case 'Accidente':
        iconUrl = 'data:image/png;base64,';
        break;
      case 'Restricción':
        iconUrl = 'data:image/png;base64,';
        break;
    case 'Obras':
        iconUrl = 'data:image/png;base64,'
        break
      case 'Evento':
        iconUrl = 'data:image/png;base64,';
        break;
      default:
        iconUrl = 'data:image/png;base64,'; // Ícono por defecto
    }

    return (
      <Marker
        key={incident.id}
        position={{
          lat: incident.location.latitude,
          lng: incident.location.longitude,
        }}
        title={incident.type}
        icon={{
          url: iconUrl, // URL del icono
          scaledSize: iconSize, // Tamaño fijo para todos los iconos
        }}
      />
    );
  })}
        </GoogleMap>
        
        
      </div>

      {/* Contenedor de la lista de incidencias */}
      <div className="w-full lg:w-2/5 px-4 lg:px-8 py-2 lg:p-6">
        <h2 className="text-2xl font-bold text-primary mb-4">Últimas Incidencias</h2>
        {loading ? (
          <p>Cargando incidencias...</p>
        ) : (
          <div className="space-y-4">
            {incidents.slice(0, 5).map((incident) => (
              <div key={incident.id} className="flex items-center p-1 lg:p-4 bg-white rounded-lg shadow-md">
                <img
                  src={getIconByType(incident.type)}
                  alt={incident.type}
                  className="w-12 h-12 mr-4 object-contain"
                />
                <div className="flex-1">
                  <h3 className="font-bold text-lg text-primary mb-1">{incident.type}</h3>
                  <p className="text-sm text-secondary mb-1">
                    {incident.address || 'Dirección no especificada'}
                  </p>
                  <p className="text-xs text-gray-500">
                    Fecha: {new Date(incident.createdAt?.seconds * 1000).toLocaleString()}
                  </p>
                </div>
              </div>
            ))}
          </div>
        )}
      </div>
    </div>
  );
};

const getIconByType = (type) => {
    switch (type) {
      case 'Evento':
        return 'data:image/png;base64,';
      case 'Accidente':
        return 'data:image/png;base64,';
      case 'Obras':
        return 'data:image/png;base64,';
      case 'Restricción':
        return 'data:image/png;base64,';
      default:
        return 'data:image/png;base64,';
    }
  };


export default CitizenView;
